    private void jButton_salvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_salvarActionPerformed

        ConfiguracaoEmail configuracaoEmail = new ConfiguracaoEmail();
        ConfEmailDAO confEmailDAO = new ConfEmailDAO();

        configuracaoEmail.setEmail(jTextField_email.getText());
        configuracaoEmail.setSenha(jPasswordField_senha.getText());
        configuracaoEmail.setHabilitaEnvio(jCheckBox_habilitaEnvio.isSelected());
        configuracaoEmail.setHorario(jFormattedTextField_horario.getText());
        configuracaoEmail.setDiaSemana(jComboBox_diaSemana.getSelectedItem().toString());

        if (jTextField_id.getText().isEmpty()) {
            if (confEmailDAO.salvar(configuracaoEmail)) {
                JOptionPane.showMessageDialog(null, "Configuração salva com sucesso!");
            } else {
                JOptionPane.showMessageDialog(null, "Erro ao tentar salvar configuração!");
            }
            
        }else{
            
            configuracaoEmail.setId(Integer.parseInt(jTextField_id.getText()));
            if(confEmailDAO.editar(configuracaoEmail)){
                JOptionPane.showMessageDialog(null, "Configuração alterada com sucesso!");
            }else {
                JOptionPane.showMessageDialog(null, "Erro ao tentar alterar configuração!");
            }
        }


    }//GEN-LAST:event_jButton_salvarActionPerformed

