    private void preencheCampos() {

        ConfEmailDAO confEmailDAO = new ConfEmailDAO();
        ConfiguracaoEmail configuracaoEmail = confEmailDAO.pegaConfiguracao();
        if (configuracaoEmail != null) {
            
            jTextField_id.setText(String.valueOf(configuracaoEmail.getId()));
            jTextField_email.setText(configuracaoEmail.getEmail());
            jCheckBox_habilitaEnvio.setSelected(configuracaoEmail.isHabilitaEnvio());
            jComboBox_diaSemana.setSelectedItem(configuracaoEmail.getDiaSemana());
            jFormattedTextField_horario.setText(configuracaoEmail.getHorario());
        }

    }

